import './style.css'
import { useState } from 'react'

const ItemCount = () => {
    const [contador, setContador] = useState(0)

    const sumar = () => {
        setContador(contador + 1)
    }
    
    const restar = () =>{
        setContador(contador - 1)
    }
  return (
    <div className='counter'>
        <div className='controllers'>
            <button on>-</button>
            <div>
                <span>{contador}</span>
            </div>
            <button>+</button>
        </div>
        <div>
            <button>Agregar al carrito</button>
        </div>
        
    </div>
  )
}

export default ItemCount
